import React, { Component } from "react";
import AddTemplate from "./Components/addTemplate";

const styles = {
    gen_box:{
        width: '300px',
        marginInline: 'auto',
        marginTop: '200px',
        backgroundColor: 'blue'
    },
    gen_but:{
        width: '100px',
        marginBottom: '20px',
        marginLeft: '100px',
        height: '30px',
        display: 'block',
        color: 'red'
    },

}

class App extends Component() {
    constructor() {
        super();
        this.state = {
            add: false,
            use: false
        }
    }

    addTemplateFunc(name){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "none"
        switch (name) {
            case "add":
                this.setState({
                    add: !this.state.add
                })
                break;
            case "use":
                this.setState({
                    use: !this.state.use
                })
                break;
        }
    }

    render() {
        return (
            <div id={"general"}>
                <div style={styles.gen_box} id={"gen_box"}>
                    <button style={styles.gen_but} onClick={() => { this.addTemplateFunc()}}>
                        Add Template
                    </button>
                    <button style={styles.gen_but}>Use Template</button>
                </div>
                {add && <AddTemplate />}
            </div>
        )
    }
}


export default App;
