import React, { Component } from "react";
import AddTemplate from "./Components/addTemplate";

class App extends Component{
    constructor() {
        super();
        this.state = {
            add: false,
            use: false
        }
        this.addTemplateFunc = this.addTemplateFunc.bind(this)
    }

    addTemplateFunc(name){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "none"
        switch (name) {
            case "add":
                this.setState({
                    add: !this.state.add
                })
                break;
            case "use":
                this.setState({
                    use: !this.state.use
                })
                break;
        }
    }

    Back(){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "block"
        this.setState({
            use: !this.state.use,
            add: !this.state.add
        })
    }

    render() {
        const { add, use } = this.state;
        return (
            <div id={"general"}>
                <div id={"gen_box"}>
                    <button onClick={() => this.addTemplateFunc("add")} className={"gen_but"}>
                        Add Template
                    </button>
                    <button className={"gen_but"}>Use Template</button>
                </div>
                {(add || use) && <button onClick={() => this.Back()}>Back</button>}
                {add && <AddTemplate />}
            </div>
        )
    }
}


export default App;
