import React, { Component } from "react"

class AddTemplate extends Component{
    constructor(props) {
        super(props);
        this.state={
            apiResponse:""
        }
        this.apiCall = this.apiCall.bind(this)
    }

    apiCall(){
        const name = document.getElementById("templateName").value
        const text = document.getElementById("text").value
        fetch("/addTemplate", {
            method:'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                templateName: name,
                templateText: text})
        })
            .then(res => res.text())
            .then(res => this.setState({apiResponse: res}))
    }

    render() {
        return(
            <div id={"addBox"}>
                <form id={"addForm"}>
                    {/*<label htmlFor="getter" className="form">Getters</label>*/}
                    {/*<input id="getter" type="email" className="form"/>*/}
                    <label htmlFor="templateName" className="form">Template Name</label>
                    <input id="templateName" className="form"/>
                    <label htmlFor="text" className={"form"}>Text</label>
                    <textarea id={"text"} className={"form"}></textarea>
                    <button id={"submit"} className={"form"} onClick={() => this.apiCall()}>Add</button>
                </form>
            </div>
        )
    }
}

export default AddTemplate
