import React, { Component } from "react"

class AddTemplate extends Component{
    constructor(props) {
        super(props);
        this.state={
            apiResponse:""
        }
        this.apiCall = this.apiCall.bind(this)
    }

    apiCall(){
        const text = document.getElementById("text").nodeValue
        fetch("/test", {
            method:'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({text: text})
        })
            .then(res => res.text())
            .then(res => this.setState({apiResponse: res}))
    }

    render() {
        return(
            <div id={"addBox"}>
                <form id={"addForm"}>
                    {/*<label htmlFor="getter" className="form">Getters</label>*/}
                    {/*<input id="getter" type="email" className="form"/>*/}
                    {/*<label htmlFor="title" className="form">Title</label>*/}
                    {/*<input id="title" className="form"/>*/}
                    <label htmlFor="text" className="form">Text</label>
                    <textarea id="text" className="form"></textarea>
                </form>
                <p>{this.state.apiResponse}</p>
                <button id={"submit"} className={"form"} onClick={() => this.apiCall()}>Add</button>
            </div>
        )
    }
}

export default AddTemplate
