import React, { Component } from "react"
import { slide as Menu } from 'react-burger-menu'

class UseTemplate extends Component{
    constructor() {
        super();
        this.state={
            apiResponse:""
        }
        this.myTemplates = this.myTemplates.bind(this)
    }

    myTemplates(){
        const div = document.getElementById("useTemplatesMenu")
        fetch("/useTemplate", {
            method:'GET',
            headers: { 'Content-Type': 'application/json' }
        })
            .then(res => res.text())
            .then(res => this.setState({apiResponse: res}))
        const templates = this.state.apiResponse
        console.log(typeof (templates))
        templates.forEach((template, index) => {
        const p = document.createElement("p")
        p.id = index+1
        p.innerHTML = template.templateName
        document.body.insertBefore(p, div)
        })
    }

    render() {
        return (
            // <Menu width={'150px'} >
            //     <p id={"defaultTemplates"} className={"templateMenu"}>Default Templates</p>
            //     <p id={"myTemplates"} className={"templateMenu"}>My Templates</p>
            // </Menu>
            <div id={"useTemplatesMenu"}>
                <p id={"defTemps"}>Default Templates</p>
                <p id={"myTemps"} onClick={this.myTemplates}>My Templates</p>
                <p>{this.state.apiResponse}</p>
            </div>
        )
    }
}

export default UseTemplate
