import React, { Component } from "react";
import AddTemplate from "./Components/addTemplate";
import UseTemplate from "./Components/useTemplate";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"
import Header from "./Components/header";

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            add: false,
            use: false
        }
        this.addTemplateFunc = this.addTemplateFunc.bind(this)
    }

    addTemplateFunc(name){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "none"
        switch (name) {
            case "add":
                this.setState({
                    add: !this.state.add
                })
                break;
            case "use":
                this.setState({
                    use: !this.state.use
                })
                break;
            default:
                throw name
        }
    }


    Back(){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "block"
        this.setState({
            use: false,
            add: false
        })
    }

    render() {
        return (
            <Router>
                <Header />
                <div id={"general"}>
                    <div id={"gen_box"}>
                        <button onClick={() => this.addTemplateFunc("add")} className={"gen_but"}>
                            <Link to="/addTemplate">Add Template</Link>
                        </button>
                        <button onClick={() => this.addTemplateFunc("use")} className={"gen_but"}>
                            <Link to="/useTemplate">Use Template</Link>
                        </button>
                    </div>
                    {(this.state.add || this.state.use) && <button onClick={() => this.Back()} id={"back"}>
                        <Link to="/">Back</Link>
                    </button>}
                    <Switch>
                        <Route path={"/addTemplate"}>
                            <AddTemplate />
                        </Route>
                        <Route path={"/useTemplate"}>
                            <UseTemplate />
                        </Route>
                    </Switch>
                </div>
            </Router>
        )
    }
}


export default App;
