import React, { Component } from "react"
import { slide as Menu } from 'react-burger-menu'

class UseTemplate extends Component{
    constructor() {
        super();
        this.state={
            apiResponse:"",
            myTempIsClick: false
        }
        this.words = []
        this.myTemplates = this.myTemplates.bind(this)
    }



    async myTemplates(){
        const div = document.getElementById("useTemplatesMenu")
        const myTemps = document.getElementById("myTemps")
        await fetch("/useTemplate", {
            method:'GET',
            headers: { 'Content-Type': 'application/json' }
        })
            .then(res => res.text())
            .then(res => this.setState({apiResponse: res}))
        let templates = (this.state.apiResponse).substring(1,this.state.apiResponse.length-1).split('},')
        templates[0] += "}"
        for(let i = 0; i<templates.length; i++) {
            let a = JSON.parse(templates[i])
            this.words.push(a)
        }
        this.words.forEach((template, index) => {
            const p = document.createElement("p")
            p.id = index + 1
            p.innerHTML = template.templateName
            div.appendChild(p)
            div.insertBefore(p, myTemps)
        })
        this.state.myTempIsClick = !this.state.myTempIsClick
    }

    render() {
        return (
            // <Menu width={'150px'} >
            //     <p id={"defaultTemplates"} className={"templateMenu"}>Default Templates</p>
            //     <p id={"myTemplates"} className={"templateMenu"}>My Templates</p>
            // </Menu>
            <div id={"useTemplatesMenu"}>
                <p id={"defTemps"}>Default Templates</p>
                <p id={"myTemps"} onClick={this.myTemplates}>My Templates</p>
            </div>
        )
    }
}

export default UseTemplate
