import React, { useState } from "react";
import AddTemplate from "./Components/addTemplate";

const styles = {
    gen_box:{
        width: '300px',
        marginInline: 'auto',
        marginTop: '200px',
        backgroundColor: 'blue'
    },
    gen_but:{
        width: '100px',
        marginBottom: '20px',
        marginLeft: '100px',
        height: '30px',
        display: 'block',
        color: 'red'
    },

}

function App() {
    const addTemplate = useState(false)
    const useTemplate = false

    function addTemplateFunc(){
        const genBox = document.getElementById("gen_box")
        genBox.style.display = "none"
        return( <AddTemplate /> )
    }
    if(addTemplate){
        return (
            <AddTemplate/>
        )
    }
    else {
        return (
            <div id={"general"}>
                <div style={styles.gen_box} id={"gen_box"}>
                    <button style={styles.gen_but} onClick={() => {
                        addTemplateFunc()
                    }}>Add Template
                    </button>
                    <button style={styles.gen_but}>Use Template</button>
                </div>

            </div>
        )
    }
}

export default App;
